#!/bin/bash

set -exo pipefail

git checkout $BRANCH
git pull origin $BRANCH
git checkout -b docs2-url-$BRANCH
find docs/ -type f -exec sed -i "s/reference\/master/reference\/$BRANCH/g" {} \;
git add docs/
git commit -m "Fix docs URLs to use '$BRANCH' instead of 'master'"
